mapper.select:
SELECT tenant_id, audit_enable
FROM app_application_tenant 
WHERE tenant_id = #{tenantId}

mapper.update:
UPDATE app_application_tenant 
SET audit_enable = #{auditEnable}, 
    audit_enable_time = NOW(), 
    audit_enable_operator = #{operator}, 
    audit_enable_reason = #{reason} 
WHERE tenant_id = #{tenantId}
