mapper.select:
select rer.id, rer.execute_id, rer.task_execute_id, rer.robot_id, rer.robot_version, re.name as robot_name, rer.start_time, rer.end_time, rer.execute_time, rer.mode, rer.result, rer.video_local_path from robot_execute_record rer left join robot_execute re on rer.robot_id = re.robot_id where rer.deleted=0 and rer.creator_id = #{entity.creatorId} and rer.tenant_id = #{entity.tenantId}
and rer.robot_id = #{entity.robotId}
and upper(re.name) like concat('%', upper(#{entity.robotName}), '%')
and rer.result = #{entity.result}
and rer.start_time >= #{entity.startTime} and rer.end_time <= #{entity.endTime}
and rer.start_time >= #{entity.startTime}
and rer.end_time <= #{entity.endTime}
order by ${entity.sortBy} desc
order by ${entity.sortBy} asc
order by start_time desc

select count(*) executeTotal, sum(result = 'robotSuccess') executeSuccess, sum(result = 'robotFail') executeFail, sum(result = 'robotCancel') executeAbort,sum(execute_time) executeTime
from robot_execute_record

    result in ('robotSuccess','robotFail','robotCancel')
    and tenant_id = #{tenantId}
    and robot_id = #{robotId}
    and start_time <= #{countTime}

select execute_log
from robot_execute_record
where execute_id = #{executeId}
  and creator_id = #{creatorId}
  and tenant_id = #{tenantId} limit 1

select *
from robot_execute_record
where deleted = 0
  and execute_id = #{executeId}
  and creator_id = #{creatorId}
  and tenant_id = #{tenantId}

select count(distinct robot_id)
from robot_execute_record
where start_time >= #{startAndEndOfDay[0]}
  and start_time < #{startAndEndOfDay[1]}
  and deleted = 0
  and id > CAST(#{lastProcessedId} AS UNSIGNED)

select count(distinct terminal_id)
from robot_execute_record
where start_time >= #{startAndEndOfDay[0]}
  and start_time < #{startAndEndOfDay[1]}
  and deleted = 0
  and id > CAST(#{lastProcessedId} AS UNSIGNED)

select max(id) id,
       robot_id
        ,
       terminal_id
from robot_execute_record
where start_time >= #{startAndEndOfDay[0]}
  and start_time < #{startAndEndOfDay[1]}
  and deleted = 0
  and id > CAST(#{lastProcessedId} AS UNSIGNED)
group by robot_id
order by id asc
    limit #{limit}
offset #{offset}

select max(id) id,
       terminal_id
from robot_execute_record
where start_time >= #{startAndEndOfDay[0]}
  and start_time < #{startAndEndOfDay[1]}
  and deleted = 0
  and id > CAST(#{lastProcessedId} AS UNSIGNED)
group by terminal_id
order by id asc
    limit #{limit}
offset #{offset}

mapper.update:
update robot_execute_record
set deleted = 1,
    update_time = now(),
    updater_id = #{userId}
where deleted = 0 
  and tenant_id = #{tenantId}
  and task_execute_id in

      #{taskExecuteId}

update robot_execute_record
set deleted = 1,
    update_time = now(),
    updater_id = #{userId}
where deleted = 0
    and tenant_id = #{tenantId}
    and creator_id = #{userId}
    and execute_id in

    #{recordId}
